import { useState } from "react"


const List=()=>
{
    const [additem,setadditem]=useState({})
    const [isedit,setisedit]=useState(false)
    const [myL,setmyL]=useState
    ([
{id:1,name:"rivka",age:34},
{id:2,name:"esther",age:16},
{id:3,name:"nachama",age:27}
  ])
const dell=(id)=>
{
setmyL(myL.filter(y=>y.id!=id))
}
 const add=(e)=>
 {
    e.preventDefault()
    if(isedit==false)
   setmyL(myL.concat(additem))
  else
  {
    let w=myL.filter(e=>e.id!=additem.id)
    w.push(additem)
    setmyL(w)
    // myL.filter(e=>e.id==additem.id)[0]=additem
    // setmyL(myL)
    // setisedit(false)
  }
 }
 const edit=(item)=>{
setadditem(item)
setisedit(true)
 }
    return <>
    <form>
        <input  type='text' placeholder="id" value={additem.id}onChange={(e)=>setadditem({...additem,id:e.target.value})}></input>
        <input type='text' placeholder="name" value={additem.name}onChange={(e)=>setadditem({...additem,name:e.target.value})}></input>
        <input type='text' placeholder="age"value={additem.age} onChange={(e)=>setadditem({...additem,age:e.target.value})}></input>
        <button type="submit" onClick={(f)=>add(f)}>send</button>
        <p>האוביקט שהכנסת------------{additem.id}  ---------{additem.name}   --------{additem.age}</p>
    </form>
    <table className="table">
       <thead>
       <tr>
        <th>coed</th>
        <th>name</th>
        <th>age</th>
       </tr>
        </thead>
<tbody>
   { myL.map(x => (
     <tr key={x.id}>
    <td>{x.id}</td> 
     <td>{x.name}</td>  
     <td>{x.age}</td>
     <td><button onClick={()=>dell(x.id)}>delete</button></td>
     <td><button onClick={()=>edit(x)}>update</button></td>
     </tr>  
))}
</tbody>
    </table>
    </>
}
export default List
