import axios from "axios";
const url = "http://localhost:8080/CustomerS/"

export const addCT = (item) => {
    return axios.post(url + "add/", item)
}
export const isexsistCT =(name, pass) => {
    return axios.get(`${url}/isexist/${name}/${pass}`)
   
}
