import { useState } from "react";

function List({load,fall}){
    const [table,settable]=useState(load('tt')||[])
    
    return (<>
        <div onLoad={fall('tt')} style={{ display: "flex", justifyContent: "center", margin: "20px" }}>
            
        </div>
        <div className="container" style={{ display: "flex", flexWrap: "wrap" }}>


            {{table}.map((x, i) => (
                <tr key={i}>

                    <div className="card" style={{ width: "300px", margin: "2rem", borderColor: "brown", borderStyle:"solid",  backgroundColor:"white" ,boxShadow:"-moz-initial"}}>
                        <img src={x.Img} style={{ width: "100%" }} alt="  " />
                        <div className="card-body">

                            <h2>שם המשחק:</h2>
                            <h3>{x.GameName}</h3>
                            
                        </div>
                    </div>

                </tr>
            ))}

        </div>

    </>)
};
export default List;
