import { useState } from "react"
import { Mylist } from "./mylist"
import { Mytable } from "./mytable"

export const Mytask=()=>{
    const [arr,setarr]=useState([
        {id:1,desc:"לארגן את החדר",done:false,time:"מחר"},
        {id:2,desc:"לדבר עם סבתא",done:false,time:"יום חמישי"},
        {id:3,desc:"לקנות בגדים",done:true,time:"לפני החגים"},
        {id:4,desc:"לראות את הסרטונים",done:false,time:"היום 15:15"},
        {id:5,desc:"לקפל כביסות",done:false,time:"היום 17:30"},
    ]) 
    //מחיקה
    const del=(id)=>{
    let u=arr.filter(x=>x.id!=id)
    setarr(u)
    }
    //הוספה
    const add=(object)=>{
        setarr(x=>x.concat(object))
    }
    //שינוי הסטטוס
    const changestatus=(index,status)=>{
        let y=[...arr]
        y[index].done=status
        setarr(y)
    }
    // בעת לחיצה על טבלה משתנה לטבלה ולהפך
    const [istable,setistable]=useState(false)
    const [islist,setislist]=useState(false)

    
   
    return <>
    <button className="btn btn-success" onClick={()=>{setislist(true);setistable(false)}}>רשימה</button>                                                  
    <button className="btn btn-success" onClick={()=>{setistable(true);setislist(false)}}>טבלה</button> 
     {islist&&<Mylist arr={arr} del={del} mycheck={changestatus}></Mylist>}
      {istable&&<Mytable arr={arr}  del={del}></Mytable>  }                                             
 </> 
}
