import { useState } from "react"
import { addCategory, loadCategory } from "../redux/actions/categoryAction"
import { useDispatch, useSelector } from "react-redux"
import { addC, updateC } from "../axios/categoryAxios"
import { useParams } from "react-router-dom"

export const AddCategore = () => {
    //קשור לבדיקות תקינות
    const [myExeption, setmyExeption] = useState({})
   

    //קשור להוספה ולעדכון
    let u = useParams().id
    const dc = useDispatch()
    let cat = useSelector(e => e.datareducerc.category)

    const [newcategory, setnewcategory] = useState(u == undefined ? {
        NameCategory: ""

    } : cat.find((x) => x._id == u))
    //קשור לבדיקות תקינות
    const checkName = (e) => {
        let txt = e.target.value
        let notValid = !txt.match("{*1}[a-z]")
        if (txt == "")
            setmyExeption({ ...myExeption, name: "שם הקטגוריה הוא חובה" })
        else if (notValid)
            setmyExeption({ ...myExeption, name: "רק אותיות!לפחות אחת" })
        else
            setmyExeption({ ...myExeption, name: true })
    }
    const addupdate = (e) => {
        debugger
        e.preventDefault()
        alert("הקטגוריה הוא" + newcategory.NameCategory)
        debugger
        if (u != "undefined")
            updateC(u, newcategory)
                .then((b) => {

                    alert("!!!!הקטגוריה עודכן בהצלחה")
                    dc(loadCategory(b.data))
                })
                .catch((err) => {
                    console.log(err);
                })
        else if (u == "undefined") {
            addC(newcategory)
                 .then((b) => {
                    dc(addCategory(b.data))
                    
        //             // da(loadGame(b.data))
                  alert("!!!!הקטגוריה נוסף בהצלחה")
                })
                .catch((err) => console.log(err))



    }
}
return <form onSubmit={(e) => addupdate(e)} >
    <input type="text" placeholder="sfbdavFb" onChange={(e) => {setnewcategory({...newcategory,NameCategory:e.target.value});checkName(e)}} onClick={(e) => checkName(e)}></input>
    <button type="submit" >הוספה\עריכה</button>
    {myExeption.name != true && <p>{myExeption.name}</p>}
</form>
    
}
