import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router-dom"
import { addG, removeG, updateG } from "../axios/gemaAxios"
import { addGame, loadGame, removeGame } from "../redux/actions/gameAction"
import { getallC } from "../axios/categoryAxios"
import { loadCategory } from "../redux/actions/categoryAction"

export const AddGame = () => {
    let u = useParams().id
    const da = useDispatch()
    let gam = useSelector(f => f.datareucerg.games)
    let cat = useSelector(e => e.datareducerc.category)

    const [newGame, setnewGame] = useState(u == undefined ? {
        GameName: "",
        CategoryCode: "",
        Price: "",
        Age: "",
        Img: "",
        Amount: ""
    } : gam.find((x) => x._id == u))
    useEffect(() => {
        //קשור לעדכון
        if (cat.length == 0) {
            getallC()
                .then((b) => {
                    da(loadCategory(b.data))
                })
                .catch((err) => {
                    console.log(err);

                })

        }
        //קשור להוספה
    }, [cat, da])

    const addupdate = (e) => {
        e.prevenDefault()
        alert("המשחק הוא" + newGame.GameName)
        debugger
        if (u != undefined)
            updateG(u, newGame)
                .then((b) => {

                    alert("!!!!המשחק עודכן בהצלחה")

                    da(loadGame(b.data))
                })
                .catch((err) => {
                    console.log(err);
                })
        else if (u == undefined) {
            addG(newGame)
                .then((b) => {
                    da(addGame(b.data))
                    // da(loadGame(b.data))
                    alert("!!!!המשחק נוסף בהצלחה")
                })
                .catch((err) => console.log(err))



        }
    }


    return <form onSubmit={(e) => addupdate(e)}>
        <label>שם המשחק</label>
        <input type="text" placeholder="הקלד את שם המשחק" value={newGame?.GameName||""} onChange={(e) => setnewGame({ ...newGame, GameName: e.target.value })}></input>
        <label>קטגוריה</label>
        <select value={newGame?.CategoryCode||""}
            onChange={(e) => setnewGame({ ...newGame, Price: e.target.value })}
        >
            {cat.map((s) => {
                (
                    <option key={s._id} value={s._id}>{s.NameCategory}</option>
                )
            })}
        </select>
        <label>מחיר</label>
        <input type="number" value={newGame?.Price||""} onChange={(e) => setnewGame({ ...newGame, Price: e.target.value })}></input>
        <label>מתאים לגילאי</label>
        <input type="text" placeholder="hhftf" value={newGame?.Age} onChange={(e) => setnewGame({ ...newGame, Age: e.target.value })}></input>
        <label>קובץ התמונה</label>
        <input type="text" value={newGame.Img} placeholder="Fdzg" onChange={(e) => setnewGame({ ...newGame, Img: e.target.value })}></input>
        <label>כמות במלאי</label>
        <input type="number" value={newGame.Amount} placeholder="ljhgk" onChange={(e) => setnewGame({ ...newGame, Amount: e.target.value })}></input>

        <button type="submit">עדכן\הוסף</button>
    </form>
}
