import { produce } from 'immer'

export const InitialStateS = {

    product: []
}

export const datareducerS = produce((state, action) => {
    switch (action.type) {

        case "ADD_TO_SAL": {
            let y = state.product.findIndex(x => x._id == action.payload._id)
            if (y == -1) {
                action.payload.Count1 = 1
                state.product.push(action.payload)
            }
            else {
                state.product[y].Count1++
                
            }
            break;
           
        }
        case "SET_AMOUNT":{
            let b = state.product.indexOf(state.product.find(x => x._id == action.payload.item._id))
            if(action.payload.element=="+")
            {
                if(state.product[b].Count1>=state.product[b].Amount)
                   alert("מצטערים אין מספיק במלאי")
                else
                state.product[b].Count1++

            }
            else{
                if(state.product[b].Count1!=0)
                    state.product[b].Count1--
                else
                state.product[b]
                
            }
            break;
        }

        
        default:
            break;
    }
}, InitialStateS)
