import { useState } from "react"
import { Personall } from "./personall"
import { useNavigate } from "react-router-dom"

export const Login=()=>{


    let mynavigate=useNavigate()

    const [user,setuser]=useState({})
    const [arrpersone,setarrpersone]=useState([
        {name:"a",password:"1"},
        {name:"b",password:"2"},
        {name:"c",password:"3"},
        {name:"d",password:"4"},
        {name:"e",password:"5"}
    ])

    const f1=()=>{
       for (let i = 0; i < arrpersone.length; i++) {
        if(arrpersone[i].name==user.name&&arrpersone[i].password==user.password) {
               mynavigate('/personall_pg')
        }
         
  
       }
 
   
    }
//  mynavigate('/login_pg')
    return <>
<h2>קומפוננטת התחברות</h2>

<input type="text" placeholder="הכנס שם " onBlur={(e)=>setuser({...user,name:e.target.value})}></input>
<input type="password" placeholder="הכנס סיסמה" onBlur={(e)=>setuser({...user,password:e.target.value})}></input>
<button onClick={()=>f1()} >sent</button>
    
    </> 
 }
