import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"
import { getAllG } from "../axios/gemaAxios"
import { loadGame } from "../redux/actions/gameAction"
import { useNavigate } from "react-router-dom"
import { addToSal } from "../redux/actions/salBuyAction"
import { getallC } from "../axios/categoryAxios"
import { loadCategory } from "../redux/actions/categoryAction"

export const HomePage = () => {
    let lgame = useSelector(p => p.datareucerg.games)
    let cat = useSelector(m => m.datareducerc.category)

    let dg = useDispatch()
    const n = useNavigate()

    useEffect(() => {
        if (lgame != null && lgame.length == 0) {
            getAllG()
                .then((x) => dg(loadGame(x.data)))
                .catch((err) => console.log(err))
        }
        if (cat != null && cat.length == 0) {
            getallC()
                .then((x) => dg(loadCategory(x.data)))
                .catch((err) => console.log(err))
        }
    }, [])

    const add1 = (x) => {
        const g = { ...x, Count1: 1 }
        dg(addToSal(g))
    }

    const filt = (e) => {
        e.preventDefault()
        getAllG()
            .then((x) => {
                dg(loadGame(x.data))
                lgame = x.data
                let CId = e.target.value
                if (CId !== "all") {
                    let y = lgame.filter(t => t.CategoryCode == CId)
                    lgame = y
                    dg(loadGame(lgame))
                } else if (CId === "all") {
                    dg(loadGame(lgame))
                }
            })
            .catch((err) => console.log(err))
    }

    return (
        <>
            <div className="container mt-5">
                <h2 className="text-center mb-4">קטגוריות משחקים</h2>
                <select className="form-select mb-4" onChange={(e) => filt(e)}>
                    <option value="all">הכל</option>
                    {cat.map((z) => {
                        return <option key={z._id} value={z._id}>{z.NameCategory}</option>
                    })}
                </select>

                <div className="row row-cols-1 row-cols-md-3 g-4">
                    {lgame.map((x, i) => (
                        <div key={i} className="col">
                            <div className="card h-100">
                                <img src={x.Img} className="card-img-top" alt={x.GameName} />
                                <div className="card-body">
                                    <h5 className="card-title">{x.GameName}</h5>
                                    <p className="card-text">
                                        {/* כאן תוכל להוסיף תיאור קצר של המשחק */}
                                        מחיר: {x.Price} ₪
                                    </p>
                                </div>
                                <div className="card-footer text-center">
                                    <button
                                        className="btn btn-primary mb-2"
                                        onClick={() => { n(`../Mdetaile/${x._id}`) }}>
                                        לפרטים נוספים
                                    </button>
                                    <button
                                        className="btn btn-success"
                                        onClick={() => { add1(x) }}>
                                        הוסף לסל
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}
