import axios from "axios"
const url = "http://localhost:8080/GameS/"
export const getAllG = () => {
    return axios.get(url + "getAll")
}
export const addG = (item) => {
    return axios.post(url + "add",item)
}

export const updateG = (id,item) => {
    return axios.put(`${url}/update/${id}`,item)
}
export const removeG = (id) => {
    return axios.delete(`${url}/remove/${id}`)
}
export const getByIdG = (id) => {
    return axios.get(`${url}/remove/${id}`)
}
export const getByCtegoryIdG = (id) => {
    return axios.get(url + "getByCtegoryId/:" + { id })
}
