import {produce} from 'immer'
export const InitialState={

 category:[
    {id:1,name:"משחקי חוץ"},
    {id:2,name:"משחקי קופסא"},
    {id:3,name:"משחקים לילדות"}
]
}

export const datareducerc=produce((state,action)=>{
    switch(action.type){
//פה כל הפונקציות צריכות להיעשות בהתאם למונגו די בי שבנינו 
//פונקציות של הוספה,עדכון,מחיקה,הצגת הכל--בעצם כל הפונקציות שיצרנו בנוד עבבור רשימת הקטגוריות
        case "ADD_CATEGORY":state.category.push(action.payload)
       // caes "UPDATE_CATEGORY":{state.category.map()פה אני רוצה למיין את הרשימה לאיפה שעומד הקטגוריה הספציפית
       //case פה אני רוצה לעעשות מחיקה 
        default:
            break;
    }
},InitialState)
