import { useNavigate } from "react-router-dom"

export const ListGame = () => {
    let lgame=useSelector(p => p.datareucerg.games)
    let dg = useDispatch()
    const n=useNavigate()
    useEffect(() => {
        debugger
        if (lgame != null && lgame.length == 0){
             getAllG()
                .then((x) => dg(loadGame(x.data)))
                .catch((err) => console.log(err))}
    }, [])
    return <div className="">
       
        
            {lgame.map((x, i) => (
                <tr key={i}>
                    <div className="card">
