import { useEffect, useState } from "react"
import { addCT, isexsistCT } from "../axios/customerAxios"
import {  useNavigate } from "react-router-dom"
import { useDispatch } from "react-redux"
import { setUserName } from "../redux/actions/loginAction"
import { adCustomer } from "../redux/actions/customerAction"

export const SignIn=()=>{
let d=useDispatch()
const n=useNavigate()
const [user,setuser]=useState()

    const logadd=()=>{
       
        debugger
        isexsistCT(user.CusName,user.Password)
        .then(x=>{
            
            if(x.data){
                alert(" הינך כבר רשום נא התחבר")
                n('../Login')
            }
            else
            {
                addCT(user)
                .then(x=>{
                    //לרידקס
                //   d(adCustomer(user))  
                    d(setUserName(x.data))
                    alert("נרשמת בהצלחה!")
                    n('../homeP')
                })
                .catch(x=>{
                    console.log(x);
                })
            }

        })
        .catch(err=>console.log(err))
        
        
    }
    return <>
     {/* <br></br>
    <h3 style={{color:"brown"}}>טופס כניסה למערכת</h3>
    <div className="container" style={{display:"flex",alignItems:"center",flexDirection:"column",borderColor: "brown", borderStyle:"solid",  backgroundColor:"white", width:"500px" ,height:"200px"}}>

<br></br>
<br />
        <input className="form-control" type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })} style={{width:"50%"}}></input>
        <br></br>
        <input className="form-control" type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })} style={{width:"50%"}}></input>
        <br />
        <button type="button" className="btn btn" onClick={() => set()} style={{borderColor: "brown", borderStyle:"solid"}}>התחבר</button>

    </div> */}
    <br></br>
    <h3 style={{color:"brown"}}>טופס התחברות למערכת</h3>

    <input className="form-control" type="text"  placeholder="הכנס שם" onBlur={(e)=>setuser({...user,CusName:e.target.value})}></input>
    <br />
    <input className="form-control" type="text" placeholder="הכנס סיסמא בה תרצה להשתמש לאורך כל הזמן" onBlur={(e)=>setuser({...user,Password:e.target.value})}></input>
    <br />
    <input className="form-control" type="text" placeholder="הכנס מספר אשראי" onBlur={(e)=>setuser({...user,CreditCard:e.target.value})}></input>
    <input className="form-control" type="number" placeholder="הכנס את שלושת הספרות שבגב הכרטיס " onBlur={(e)=>setuser({...user,cvv:e.target.value})}></input>
    <input className="form-control" type="text" placeholder="הכנס את תוקף האשראי" onBlur={(e)=>setuser({...user,Tokef:e.target.value})}></input>
    <button className="form-control" onClick={()=>logadd()}>להרשמה</button>
    
    </>
}
