import { useState } from "react"
import { useDispatch } from "react-redux"
import { setMan, setUserName } from "../redux/actions/loginAction"
import { isexsistCT } from "../axios/customerAxios"
import { NavLink, useNavigate } from "react-router-dom"

export const Login = () => {
    const d = useDispatch()
    const n = useNavigate()
    const [user, setuser] = useState({})
    const set = () => {
debugger
        if (user.name == "me" && user.password == 11)
            d(setMan(true))

        else {
            d(setMan(false))
            {
                isexsistCT(user.name, user.password)
                    .then(x => {

                        if (x.data) {
                            alert("התחברת בהצלחה")
                            n('../homeP')
                            console.log("bbbb" + x.data);

                            d(setUserName(x.data))
                        }

                        else {
                            alert("אינך רשום במערכת נא התחבר")
                            n('../sign')
                        }
                    })

                    .catch((err) => { console.log(err) })








            }


        }


    }



    return <div className="container" style={{display:"flex",alignItems:"center",flexDirection:"column",borderColor: "brown", borderStyle:"solid",  backgroundColor:"white", width:"60%" ,height:""}}>
<h3>טופס כניסה למערכת</h3>
<br></br>
        <input className="form-control" type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })} style={{width:"50%"}}></input>
        <br></br>
        <input className="form-control" type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })} style={{width:"50%"}}></input>
        <button onClick={() => set()}>התחבר</button>

    </div>
}
