import { useState } from "react"
import { useDispatch } from "react-redux"
import { setMan, setUserName } from "../redux/actions/loginAction"
import { isexsistCT } from "../axios/customerAxios"
import { NavLink } from "react-router-dom"

export const Login = () => {
    const d = useDispatch()
    const [user, setuser] = useState({})
    const set = (user1, name) => {

        if (user1.name == "me" && user1.password == 11)
            d(setMan(true))

        else {
            d(setMan(false))
            {
                isexsistCT(name, user1.password) == true &&
                <li className="nav-item">
                    <p>אינך רשום במערכת</p>
                    <NavLink className="nav-link" to="sign">להרשמה לחץ כאן</NavLink>
                </li>
            }
        }
        d(setUserName(name))
    }




return <div>
    <input type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })}></input>
    <input type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })}></input>
    <button onClick={() => set(user, user.name)}>התחבר</button>

</div>
}
