import axios from "axios"
const url = "http://localhost:8080/GameS/"
export const getAllG = () => {
    return axios.get(url + "getAll")
}
export const addG = () => {
    return axios.post(url+"add") 
}

export const update=(id)=>{
return axios.put(url+"update/ :"+{id})
}
