import axios from 'axios';

const apiUrl = "http://localhost:3000/"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/getAll`)
    return result.data;
  },

  addTask: async (item) => {
    console.log('addTask', item)
    const result = await axios.post(`${apiUrl}/add`, item)
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete })

    const result = await axios.put(`${apiUrl}/update/${id}`,isComplete)

    return result.data;
  },

  deleteTask: async (id) => {
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/del/${id}`)

  }
};
