import { useDispatch, useSelector } from "react-redux"
import { setAmount } from "../redux/actions/salBuyAction"
import { useState } from "react"
import { saveB } from "../redux/actions/buyAction"
import { saveBuyB } from "../axios/buyAxios"
import { useNavigate } from "react-router-dom"

export const Sal = () => {
    const ds = useDispatch()
    const prod = useSelector(k => k.datareducerS.product)
    const currnt = useSelector(u => u.datareducercu.currntCuc)
    const logn=useSelector(s=>s.datareducerL.userName.CusName)
    const n=useNavigate()



    const finish = () => {
        
        debugger
        if(logn==" "){
            alert("אתה לא מחובר")
            n('../Login')
        }
        else{
        const objj = {
            CusCode: logn._id,
            Date: Date(),
            all: prod
        }
        saveBuyB(objj)
            .then((x) => {
                alert("הקניה הושלמה בהצלחה")
                ds(saveB(x.data))
            })
            .catch((err)=>console.log(err)
            )
        }
    }
    return <>
        <table >
            <thead>
                <tr>
                    <th>קוד</th>
                    <th>שם</th>
                    <th>כמות</th>
                    <th>מחיר</th>
                </tr>
                </thead>
                <tbody>

                    {prod.map((x, i) => (
                        <tr key={i}>
                            <td>{x._id}</td>
                            <td>{x.GameName}</td>
                            <td>{x.Count1}</td>
                            <td>{(x.Count1 * x.Price)}</td>
                            <button onClick={() => { ds(setAmount(x, "+")); debugger }}>+</button>
                            <button onClick={() => ds(setAmount(x, "-"))}>-</button>

                        </tr>))}
                </tbody>
          
        </table>
        <button onClick={() => finish()} >סיום קניה</button>
    </>
}
