import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getallC } from "../axios/categoryAxios"
import { loadCategory } from "../redux/actions/categoryAction"

export const ListCategory = () => {
    let lcat = useSelector(k => k.datareducerc.category)
    let md = useDispatch()
    useEffect(() => {

        if (lcat != null && lcat.length == 0) {
            getallC()
            .then((x) => md(loadCategory(x.data)))
             .catch((err) => console.log(err))  
            
        }


    }, [])

    return <table className="table container">
        <thead>
            <tr>
                <th>קוד הקטגוריה</th>
                <th>שם הקטגוריה</th>
            </tr>
        </thead>
        <tbody>
            {lcat.map((x, i) => (
                <tr key={i}>
                    <td>{x._id}</td>
                    <td>{x.NameCategory}</td>
                    <button onClick={()=>remo(x._id)}>עדכון </button>
                    <button >מחיקה</button>
                </tr>
            ))}
        </tbody>
    </table>
}
