import { useDispatch, useSelector } from "react-redux"
import { setAmount } from "../redux/actions/salBuyAction"
import { useState } from "react"
import { saveB } from "../redux/actions/buyAction"
import { saveBuyB } from "../axios/buyAxios"
import { useNavigate } from "react-router-dom"

export const Sal = () => {
    const ds = useDispatch()
    const prod = useSelector(k => k.datareducerS.product)
    const logn=useSelector(s=>s.datareducerL.currentUser._id)
    const n=useNavigate()



    const finish = () => {
        
        debugger
        if(!logn){
            alert("אתה לא מחובר")
            n('../Login')
        }
        else{
        const objj = {
            CusCode: logn,
            Date: Date(),
            all: prod
        }
      
        
        const totalAmount = prod.reduce((acc, item) => acc + item.Count1 * item.Price, 0);  
         alert("סכום הקניה הוא:"+totalAmount)
        saveBuyB(objj)
            .then((x) => {
                alert("הקניה הושלמה בהצלחה")
                console.log("x",x)
                ds(saveB(x.data))
            })
            .catch((err)=>console.log(err)
            )
          

        
        }
    }
    return <>
        <table className="table container"  >
            <thead>
                <tr>
                    <th>קוד</th>
                    <th>שם</th>
                    <th>כמות</th>
                    <th>מחיר</th>
                </tr>
                </thead>
                <tbody>

                    {prod.map((x, i) => (
                        <tr key={i}>
                            <td><img style={{width:"50px"}} src={x.Img} alt="" /></td>
                            <td>{x._id}</td>
                            <td>{x.GameName}</td>
                            <td>{x.Count1}</td>
                            <td>{(x.Count1 * x.Price)}</td>
                            <button onClick={() => { ds(setAmount(x, "+")); debugger }}>+</button>
                            <button onClick={() => ds(setAmount(x, "-"))}>-</button>

                        </tr>))}
                </tbody>
          
        </table>
        <button className="btn btn"  style={{ borderColor: "brown", borderStyle: "solid" }}onClick={() => finish()} >סיום קניה</button>
    </>
}
