import { useState } from "react"
import { Row } from "../../row/row"
import { Myform } from "../../myform/myform"

export const  Showtable=()=>{
    const [itemf,setitemf ]=useState({})
const [listfrute,setlistfrute]=useState([
    {name:"תפוח",color:"green"},
    {name:"ענבים",color:"green"},
    {name:"לימונים",color:"yellow"},
    {name:"אפרסקים",color:"red"}
])

const del=(name)=>{
    
   setlistfrute(listfrute.filter(r=>r.name!=name))
}
const add=(event)=>{
    event.preventDefault()
setlistfrute(listfrute.concat(itemf))
}
const edit=(itemclick)=>{
setitemf(itemclick)
}
return <> 
<Myform onadd={add} setitemf={setitemf} itemf={itemf}></Myform>
<table className="table">
<thead>
   <tr>
    <th>שם</th>
    <th>צבע</th>
 
    </tr> 
</thead>
<tbody>
<tr>
    {listfrute.map(m=>(
         <Row key={m.name} myR={m} ocdel={del}></Row>
        ))
  
    }
</tr>
</tbody>
</table></>
 }
