import { addCategory } from "../redux/actions/categoryAction"
import { useDispatch, useSelector } from "react-redux"

export const AddCategore = () => {
    //קשור לבדיקות תקינות
    const [myExeption, setmyExeption] = useState({})
    const [ct,setct]=useState({})

    //קשור להוספה ולעדכון
    let u = useParams().id
    const dc=useDispatch()
     let cat = useSelector(e => e.datareducerc.category)

     const [newategory, setnewategory] = useState(u == undefined ? {
        
    
     } : cat.find((x) => x._id == u))
    const checkName = (e) => {
        let txt = e.target.value
        let notValid = !txt.match("{*1}[a-z]")
        if (txt == "")
            setmyExeption({ ...myExeption, name: "שם הקטגוריה הוא חובה" })
        else if (notValid)
            setmyExeption({ ...myExeption, name: "רק אותיות!לפחות אחת" })
        else
            setmyExeption({ ...myExeption, name: true })
    }

    return <form onSubmit={(e)=>addupdate(e)} >
        <input type="text" placeholder="sfbdavFb" onBlur={(e)=>setct({...ct,NameCategory:e.target.value})} onClick={(e) => checkName(e)}></input>
        <button type="submit" >הוספה\עריכה</button>
       {myExeption.name!=true&& <p>{myExeption.name}</p>}
    </form>
    
}
