import { useState } from "react"
import { useDispatch } from "react-redux"
import { setMan, setUserName } from "../redux/actions/loginAction"
import { isexsistCT } from "../axios/customerAxios"
import { NavLink, useNavigate } from "react-router-dom"

export const Login = () => {
    const d = useDispatch()
    const n = useNavigate()
    const [user, setuser] = useState({})
    const set = () => {
debugger
        if (user.name == "me" && user.password == 11)
            d(setMan(true))

        else {
            d(setMan(false))
            {
                isexsistCT(user.name, user.password)
                    .then(x => {

                        if (x.data) {
                            alert("התחברת בהצלחה")
                            n('../homeP')
                            console.log("bbbb" + x.data);

                            d(setUserName(x.data))
                        }

                        else {
                            alert("אינך רשום במערכת נא התחבר")
                            n('../sign')
                        }
                    })

                    .catch((err) => { console.log(err) })








            }


        }


    }



    return<>
    <br></br>
    <h3 style={{color:"brown"}}>טופס כניסה למערכת</h3>
    <div className="container" style={{display:"flex",alignItems:"center",flexDirection:"column",borderColor: "brown", borderStyle:"solid",  backgroundColor:"white", width:"500px" ,height:"200px"}}>

<br></br>
<br />
        <input className="form-control" type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })} style={{width:"50%"}}></input>
        <br></br>
        <input className="form-control" type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })} style={{width:"50%"}}></input>
        <br />
        <button type="button" className="btn btn" onClick={() => set()} style={{borderColor: "brown", borderStyle:"solid"}}>התחבר</button>

    </div>
    </>
}
