import { produce } from 'immer'

export const InitialStateS = {

    product: []
}

export const datareducerS = produce((state, action) => {
    switch (action.type) {

        case "ADD_TO_SAL": {
            let y = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
            if (y == -1) {
                action.payload.Count111 = 1
                state.product.push(action.payload)
            }
            else {
                state.product[y].Count111++
            }
             alert(state.product[y].Count11)
            break;
           
        }
        case "SET_AMOUNT":{
            let b = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
            if(action.payload.element=="+")
            {
                if(state.product[b].Count11>=state.product[b].Amount)
                   alert("מצטערים אין מספיק במלאי")
                else
                state.product[b].Count111++

            }
            else{
                if(state.product[b]!=0)
                    state.product[b].Count111--
                else
                state.product.remove((state.product[b]))
            }
        }

        
        default:
            break;
    }
}, InitialStateS)
