import {produce} from 'immer'

export const InitialStateB={

buys:[]
}

export const datareducerB=produce((state,action)=>{
    switch(action.type){
        
        case "SAVE_BUY":state.buys.push(action.payload); break;
    
        case "GET_BY_ID":state.buys=action.payload
        default:
            break;
    }
},InitialStateB)
