import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"
import { getAllG } from "../axios/gemaAxios"
import { loadGame } from "../redux/actions/gameAction"
import { useNavigate } from "react-router-dom"
import { addToSal } from "../redux/actions/salBuyAction"
import { getallC } from "../axios/categoryAxios"
import { loadCategory } from "../redux/actions/categoryAction"
export const HomePage = () => {
    let lgame = useSelector(p => p.datareucerg.games)
    let cat = useSelector(m => m.datareducerc.category)

    let dg = useDispatch()
    const n = useNavigate()
    useEffect(() => {
        debugger
        if (lgame != null && lgame.length == 0) {
            getAllG()
                .then((x) => dg(loadGame(x.data)))
                .catch((err) => console.log(err))
        }
        if (cat != null && cat.length == 0) {
            getallC()
                .then((x) => dg(loadCategory(x.data)))
                .catch((err) => console.log(err))
        }


    }, [])

    const add1 = (x) => {
        debugger
        const g = { ...x, Count1: 1 }
        dg(addToSal(g))
    }

    const filt = (e) => {
         debugger
         e.preventDefault()
         getAllG()
            .then((x) => {
                dg(loadGame(x.data))
                lgame = x.data
                let CId = e.target.value
                if (CId != "all") {
                    let y = lgame.filter(t => t.CategoryCode == CId)
                    lgame = y
                    dg(loadGame(lgame))
                }
                else if (CId == "all")
                    dg(loadGame(lgame))
            })
            .catch((err) => console.log(err))



    }
    return (<>
    <div style={{display:"flex",justifyContent:""}}>
        <select

            onChange={(e) => filt(e)}>
            <option value="all">הכל</option>
            {cat.map((z) => {
                return <option key={z._id} value={z._id}>{z.NameCategory}</option>
            })}
        </select>
        </div>
        <div className="container" style={{ display: "flex", flexWrap: "wrap" }}>


            {lgame.map((x, i) => (
                <tr key={i}>

                    <div className="card" style={{ width: "300px" }}>
                         <img src={x.Img} style={{ width: "100%" }} alt="  " />
                        <div className="card-body">
                           
                            <h2>שם המשחק:</h2>
                            <h3>{x.GameName}</h3>
                            <button onClick={() => { n(`../Mdetaile/${x._id}`); alert(x._id) }}>לפרטים נוספים</button>
                            <button onClick={() => { add1(x); debugger }} >הוסף לסל</button>
                        </div>
                    </div>

                </tr>
            ))}

        </div>

    </>)
}
