import axios from "axios"
const url="http://localhost:8080/CategoryS/"
export const getallC=()=>{
    return axios.get(url+"getAll")
}
export const addC=(item)=>{
    return axios.post(url+"add",item)
}
export const updateC=(id,item)=>{
    return axios.put(url+"update/:"+{id},item)
}
export const deleteC=(id)=>{
    return axios.delete(url+"remove/:"+{id})
}
