import { produce } from 'immer'

export const InitialStateS = {

    product: []
}

export const datareducerS = produce((state, action) => {
    switch (action.type) {

        case "ADD_TO_SAL": {
            let y = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
            if (y == -1) {
                action.payload.Count = 1
                state.product.push(action.payload)
            }
            else {
                state.product[y].Count++
            }
             alert(state.product[y].Count)
            break;
           
        }
        case "SET_AMOUNT":{
            let b = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
            if(action.payload.element=="+")
            {
                if(state.product[b].Count>=state.product[b].Amount)
                   alert("מצטערים אין מספיק במלאי")
            }
        }

        
        default:
            break;
    }
}, InitialStateS)
