import { useState } from "react"

export const AddCategore = () => {
    const [myExeption, setmyExeption] = useState({})
    const checkName = (e) => {
        let txt = e.target.value
        let notValid = !txt.match("{*1}[א-ת]")
        if (txt == "")
            setmyExeption({ ...myExeption, name: "שדה חובה" })
        else if (notValid)
            setmyExeption({ ...myExeption, name: "רק אותיות!לפחות אחת" })
        else
            setmyExeption({ ...myExeption, name: true })
    }

    return <form>
        <input type="number" ></input>
        <input type="text" onBlur={(e) => checkName(e)}></input>
        <p></p>
    </form>
}
