import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { deleteC, getallC } from "../axios/categoryAxios"
import { deleteCategory, loadCategory } from "../redux/actions/categoryAction"

export const ListCategory = () => {
    let lcat = useSelector(k => k.datareducerc.category)
    let md = useDispatch()
    useEffect(() => {

        if (lcat != null && lcat.length == 0) {
            getallC()
            .then((x) => md(loadCategory(x.data)))
             .catch((err) => console.log(err))  
            
        }


    }, [])
const remo=(id)=>{
    debugger
      deleteC(id)
                .then((x) => md(deleteCategory(x._id)))
                .catch((err) => console.log(err));
            getallC()
                .then((x) => md(loadCategory(x.data)))
                .catch((err) => console.log(err))
}
const upd=(id)=>{
    n(`../a/${id}`)
}
    return <table className="table container">
        <thead>
            <tr>
                <th>קוד הקטגוריה</th>
                <th>שם הקטגוריה</th>
            </tr>
        </thead>
        <tbody>
            {lcat.map((x, i) => (
                <tr key={i}>
                    <td>{x._id}</td>
                    <td>{x.NameCategory}</td>
                    <td>
                    <button onClick={()=>upd(x._id)} >עדכון </button>
                    <button onClick={()=>remo(x._id)}>מחיקה</button>
                     </td>
                </tr>
            ))}
        </tbody>
    </table>
}
