import { useEffect, useState } from "react"
import { addCT, isexsistCT } from "../axios/customerAxios"
import { NavLink } from "react-router-dom"
import { useDispatch } from "react-redux"
import { setUserName } from "../redux/actions/loginAction"

export const SignIn=()=>{
let d=useDispatch()
    useEffect(()=>{
        
            
        },[])
        let res
    const logadd=(us)=>{
        {isexsistCT() &&<li className="nav-item">
            <p>אתה כבר רשום במערכת נא התחבר</p>     
            <NavLink className="nav-link" to="Login">התחברות</NavLink>
        </li>}
        if(isexsistCT(us.CusName,us.Password)==false){
         d(addCT(us))
         
        }
        d(setUserName(us.CusName))
        
    }
    const [user,setuser]=useState()
    return <>
    <br></br>
    <input type="text" placeholder="הכנס שם" onBlur={(e)=>setuser({...user,CusName:e.target.value})}></input>
    <input type="text" placeholder="הכנס סיסמא בה תרצה להשתמש לאורך כל הזמן" onBlur={(e)=>setuser({...user,Password:e.target.value})}></input>
    <input type="text" placeholder="הכנס מספר אשראי" onBlur={(e)=>setuser({...user,CreditCard:e.target.value})}></input>
    <input type="number" placeholder="הכנס את שלושת הספרות שבגב הכרטיס " onBlur={(e)=>setuser({...user,cvv:e.target.value})}></input>
    <input type="text" placeholder="הכנס את תוקף האשראי" onBlur={(e)=>setuser({...user,Tokef:e.target.value})}></input>
    <button onClick={()=>logadd(user)}>להרשמה</button>
    
    </>
}
