import { useState } from "react"
import { useParams } from "react-router-dom"



export const Showimg=()=>{
  const [optin,setoptin]=useState("'Times New Roman', Times, serif")
    let myparameter=useParams()
    debugger
return<>
{/* 1:ניתפוס מה בחרנו לתוך option */}
 <select onChange={(e)=>setoptin(e.target.value)}>
    <option value="'Times New Roman', Times, serif">כתב 1</option>
    <option value="'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif"> כתב 2</option>
 </select>
 {/* 2:נעצב תגית באופציה הניבחרת */}
 <p style={{fontFamily:optin}}>שלום וברכה</p>
<input type="color" />
 {/* הצגת תמונה */}
<img src={`../../img/${myparameter.myparameter}`}></img>






</>

}
