import { useState } from "react"
import { useDispatch } from "react-redux"
import { setMan, setUserName } from "../redux/actions/loginAction"
import { isexsistCT } from "../axios/customerAxios"
import { NavLink, useNavigate } from "react-router-dom"

export const Login = () => {
    const d = useDispatch()
    const n = useNavigate()
    const [user, setuser] = useState({})
    const set = () => {

        if (user.name == "me" && user.password == 11)
            d(setMan(true))

        else {
            d(setMan(false))
            {
                isexsistCT(user.name, user.Password)
                    .then(x => {

if(x.d)
                        alert("התחברת בהצלחה")
                        console.log("bbbb" + x.data);

                        d(setUserName(x.data))
                    })
                    .catch( (err) =>{
                        console.log(err)
                        alert("אינך רשום במערכת נא התחבר")
                         n('../sign')
            })


            }


        }

    }







    return <div>
        <input type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })}></input>
        <input type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })}></input>
        <button onClick={() => set()}>התחבר</button>

    </div>
}
