import {produce} from 'immer'
export const InitialStateL={

isManager:false,
currentUser:{}

}

export const datareducerL=produce((state,action)=>{
    switch(action.type){
        case "SET_MAN":state.isManager=action.payload
         case "SET_USERNAME": state.currentUser=action.payload  
        
        default:
            break;
    }
},InitialStateL)
