import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router-dom"
import { addG, removeG, updateG } from "../axios/gemaAxios"
import { addGame, loadGame, removeGame } from "../redux/actions/gameAction"
import { getallC } from "../axios/categoryAxios"
import { loadCategory } from "../redux/actions/categoryAction"

export const AddGame = () => {
    let u = useParams().id
    const da = useDispatch()
    let gam = useSelector(f => f.datareucerg.games)
    let cat = useSelector(e => e.datareducerc.category)
    let y = gam.find(b => b._id == u.id)

    const [newGame, setnewGame] = useState(id == undefined ? { GameName: "" } : gam.find((x) => x._id == id))
    useEffect(() => {
        if (y.length == 0) {
            getallC()
                .then((b) => {
                    da(loadCategory(b.data))
                })
                .catch((err) => {
                    console.log(err);

                })

        }
    }, [cat, da])

    const addupdate = (e) => {
        e.prevenDefault()
        alert("המשחק הוא" + newGame.GameName)
        debugger
        if (u != undefined)
            updateG(u, newGame)
                .then((b) => {
                    da(loadGame(b.data))
                    alert("!!!!המשחק עודכן בהצלחה")
                })
                .catch((err) => {
                    console.log(err);
                })
        else {
            addG(newGame)
                .then((b) => {
                    da(addGame(b.data))
                    da(loadGame(b.data))
                    alert("!!!!המשחק נוסף בהצלחה")
                })
                .catch((err) => {
                    console.log(err);
                })

        }

        return <form onSubmit={(e)=>addupdate(e)}>
            <label>שם המשחק</label>
            <input type="text" placeholder="הקלד את שם המשחק" value={newGame.GameName} onChange={(e) => setnewGame({ ...newGame, GameName: e.target.value })}></input>
            <label>קטגוריה</label>
            <select placeholder={y.NameCategory}
            >
        {cat.map((s, i) => {
            (
                <option key={i} placeholder={s.NameCategory}>{s.NameCategory} onChange={(e) => setnewGame({ ...newGame, NameCategory: e.target.value })}</option>
            )
        })}
    </select>
            <label>מחיר</label>
            <input type="number" placeholder={newGame.Price || ""} onChange={(e) => setnewGame({ ...newGame, Price: e.target.value })}></input>
            <label>מתאים לגילאי</label>
            <input type="text" placeholder={newGame.Age || ""} onChange={(e) => setnewGame({ ...newGame, Age: e.target.value })}></input>
            <label>קובץ התמונה</label>
            <input type="text" placeholder={newGame.Img || ""} onChange={(e) => setnewGame({ ...newGame, Img: e.target.value })}></input>
            <label>כמות במלאי</label>
            <input type="number" placeholder={newGame.Amount || ""} onChange={(e) => setnewGame({ ...newGame, Amount: e.target.value })}></input>

            <button onClick={() => addupdate(u.id, u.isup)}>עדכן\הוסף</button>
        </form>
    }
}
