import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router-dom"
import { addG, removeG, updateG } from "../axios/gemaAxios"
import { loadGame, removeGame } from "../redux/actions/gameAction"

export const AddGame = ({ id, isup }) => {
    let u = useParams()
    const da = useDispatch()
    let gam = useSelector(f => f.datareucerg.games)
    let y = gam.find(b => b._id == u.id)
    let cat = useSelector(e => e.datareducerc.category)
    const [newGame, setnewGame] = useState({
        GameName: "",
        Age: "",
        Price: "",
        _id: "",
        NameCategory: "",
        Amount: "",
        Img: ""

    })
    useEffect(()=>{
if(y.length>0&&newGame.GamName!==y[0].GamName){
    setnewGame({
        GameName: y,
        Age: "",
        Price: "",
        _id: "",
        NameCategory: "",
        Amount: "",
        Img: ""
    })
}
    },[y])

    const addupdate = () => {
        if (isup == true) {
            updateG(newGame)
                .then((x) => da(loadGame(x.data)))
                .catch((err) => console.log(err))
            removeG(y._id)
                .then((x) => da(removeGame(x.data)))

        }
        else {
            addG()
        }
    }

    return <>
        <label>שם המשחק</label>
        <input type="text" value={y.GameName} onChange={(e) => setnewGame({ ...newGame, GameName: e.target.value })}></input>
        <label>קטגוריה</label>
        <select placeholder={y.NameCategory}>
            {cat.map((s, i) => {
                (
                    <option key={i} placeholder={s.NameCategory}>{s.NameCategory} onChange={(e) => setnewGame({ ...newGame, NameCategory: e.target.value })}</option>
                )
            })}
        </select>
        <label>מחיר</label>
        <input type="number" placeholder={y.Price} onChange={(e) => setnewGame({ ...newGame, Price: e.target.value })}></input>
        <label>מתאים לגילאי</label>
        <input type="text" placeholder={y.Age} onChange={(e) => setnewGame({ ...newGame, Age: e.target.value })}></input>
        <label>קובץ התמונה</label>
        <input type="text" placeholder={y.GamName} onChange={(e) => setnewGame({ ...newGame, Img: e.target.value })}></input>
        <label>כמות במלאי</label>
        <input type="number" placeholder={y.GamName} onChange={(e) => setnewGame({ ...newGame, Amount: e.target.value })}></input>

        <button onClick={() => addupdate()}>עדכן\הוסף</button>
    </>
}
