import { useSelector } from "react-redux"
import { useParams } from "react-router-dom"

export const MoreDetailes=()=>{
    const p=useParams().id
    let lgame=useSelector(p => p.datareucerg.games)
    const x=lgame.find((b)=>b._id==p)
const a=x.Amount
    return <div className="card">
        <h4>{x.GameName}</h4>
        <img src={x.Img}></img>
        <h2>מחיר:      {x.Price}</h2>
        <p>מתאים לגילאי        {x.Age}</p>
        {a<3&&<p>המלאי אוזל</p>}



    </div>
}
