import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"
import { getAllG } from "../axios/gemaAxios"
import { loadGame } from "../redux/actions/gameAction"
import { useNavigate } from "react-router-dom"

export const ListGame = () => {
    let lgame=useSelector(p => p.datareucerg.games)
    let dg = useDispatch()
    const n=useNavigate()
    useEffect(() => {
        debugger
        if (lgame != null && lgame.length == 0){
             getAllG()
                .then((x) => dg(loadGame(x.data)))
                .catch((err) => console.log(err))}
    }, [])
    return <table className="table container">
       
        <tbody>
            {lgame.map((x, i) => (
                <tr key={i}>
                    <td>{x._id}</td>
                    <td>{x.GameName}</td>
                    <td>{x.Price}</td>
                    <td>{x.age}</td>
                    <td>{x.codeC}</td>
                    <td>{x.img}</td>
                    <button onClick={()=>n('../Mdetaile')}>לפרטים נוספים</button>
                    <button >הוסף לסל</button>
                </tr>
            ))}
        </tbody>
    </table>
}
