import { useState } from "react"
import { useSelector } from "react-redux"
import { useParams } from "react-router-dom"

export const AddGame=(id)=>{
    let u=useParams()
    let gam=useSelector(f=>f.datareucerg.games)
    let y =gam.find(b => b._id == u.id)
    let cat=useSelector(e=>e.datareducerc.category)
const [newGame,setnewGame]=useState({})


return<>
<label>שם המשחק</label>
<input type="text" value={y.GameName} onChange={(e)=>setnewGame({...newGame,GameName:e.target.value})}></input>
<label>קטגוריה</label>
<select value={y.NameCategory}>
  {cat.map((s,i)=>{(
    <option key={i} value={s.NameCategory}>{s.NameCategory} onChange={(e)=>setnewGame({...newGame,NameCategory:e.target.value})}</option>
 )})}   
</select>
<label>מחיר</label>
<input type="number" value={y.Price} onChange={(e)=>setnewGame({...newGame,Price:e.target.value})}></input>
<label>מתאים לגילאי</label>
<input type="text" value={y.Age} onChange={(e)=>setnewGame({...newGame,NameCategory:e.target.value})}></input>
<label>קובץ התמונה</label>
<input type="text" value={y.GamName}></input>
<label>כמות במלאי</label>
<input type="number" value={y.GamName}></input>

<button>עדכן\הוסף</button>
</>
}
