    const d = useDispatch()
    const [user, setuser] = useState({})
    const set = (user1,name) => {
        ed
        if (user1.name == "me" && user1.password == 11)
            d(setMan(true))

        else {
            d(setMan(false))
        }
        d(setUserName(name))
    }
    return <div>
        <input type="text" placeholder="הכנס שם" onBlur={(e) => setuser({ ...user, name: e.target.value })}></input>
        <input type="password" placeholder="הכנס סיסמא" onBlur={(e) => setuser({ ...user, password: e.target.value })}></input>
        <button onClick={()=>{set(user,user.name)}}>התחבר</button>

    </div>


}
