import { produce } from 'immer'

export const InitialStateS = {

    product: []
}

export const datareducerS = produce((state, action) => {
    switch (action.type) {

        case "ADD_TO_SAL": {
            let y = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
            if (y == -1) {
                action.payload.Amount = 1
                state.product.push(action.payload)
            }
            else {
                state.product[y].Amount++
            }
        }

        case "ADD_AM":
            {
                let y = state.product.indexOf(state.product.find(x => x._id == action.payload._id))
                state.product[action.payload.index] = action.payload.Amount

            }
        default:
            break;
    }
}, InitialStateS)
